import React from "react";
import { connect } from "react-redux";
import heartIcon from '../icons/heart.png';
import heartFilledIcon from '../icons/heart-filled.png';
import commentIcon from '../icons/comment.png';
import shareIcon from '../icons/send.png';
import bookmarkIcon from '../icons/bookmark.png';
// this.state = {isFilledHeartIcon: false};

// const toggleFilledHeartIcon = () =>{
//     this.setState(prevState => ({
//       isFilledHeartIcon: !prevState.isFilledHeartIcon
//     }));
// }
class UserPost extends React.Component {
  constructor(props) {
    super(props);
    this.state = {isFilledHeartIcon: true};
      // This binding is necessary to make `this` work in the callback
      this.toggleFilledHeartIcon = this.toggleFilledHeartIcon.bind(this);
  }
// const UserPost = ({ userPost }) => (
  render() {
    return (
    <div className="post-container__item">
      <div className="post-container__item__user-data">
        <img alt="user photo" src={userPost.caption.from.profile_picture} cross-origin="" />
        <div className="post-container__item__user-data__username">
          {userPost.caption.from.username}
        </div>
      </div>
      <a href={userPost.link} target="_blank">
        <img alt="user post" src={userPost.images.low_resolution.url} cross-origin="" />
      </a>
      <div className="post-container__item__action">
        <button onClick={toggleFilledHeartIcon}>
          <img alt="like" src={this.state.isFilledHeartIcon ? heartIcon : heartFilledIcon}></img>
        </button>
        <button>
          <img alt="like" src={commentIcon}></img>
        </button>
        <button>
          <img alt="like" src={shareIcon}></img>
        </button>
        <button>
          <img alt="like" src={bookmarkIcon}></img>
        </button>
      </div>
      <div className="post-container__item__caption">
        <p>
          <span>
            <strong>{userPost.caption.from.username}</strong>
          </span>
          &nbsp;
          {userPost.caption.text}
        </p>
      </div>
      <div className="post-container__item__comment">
        {userPost.user_comments.map((comment, index) => (
          <p key={index}>
            <span>
              <strong>{comment.username}</strong>
            </span>
            &nbsp;
            {comment.text}
          </p>
        ))}
      </div>
    </div>
  )}
}

export default connect(
  null,
)(UserPost);
